const cartLinks = document.querySelectorAll(".useraction-menu__button_type_cart"),
  cartModal = document.querySelector(".modal-cart"),
  cartCloseButton = cartModal.querySelector(".modal-close"),
  cartModalButton = cartModal.querySelector(".modal-cart__button");
for (let t of cartLinks) t.addEventListener("click", function (t) {
  t.preventDefault(), cartModal.classList.add("modal-show-cart")
});
cartModalButton.addEventListener("click", function (t) {
  t.preventDefault(), cartModal.classList.remove("modal-show-cart")
}), cartCloseButton.addEventListener("click", function (t) {
  t.preventDefault(), cartModal.classList.remove("modal-show-cart")
}), window.addEventListener("keydown", function (t) {
  27 === t.keyCode && cartModal.classList.contains("modal-show-cart") && (t.preventDefault(), cartModal.classList.remove("modal-show-cart"))
});